// import logo from '../../logo.svg';
import './App.css';
import React, {Component} from "react";
import Accommodations from "../Accommodations/accommodations";
import websiteService from "../../repository/websiteRepository";
import {BrowserRouter as Router, Redirect, Route, Routes} from 'react-router-dom'
import Categories from "../Categories/categories";
import Hosts from "../Hosts/hosts";
// import Header from '../Header/header';

class App extends Component{
  constructor(props) {
    super(props);
    this.state = {
      accommodations:[],

    }
  }

  render() {
    return(
        <div>
            <Router>

                <main>
                    <div className="container">
                        <Routes>
                        <Route path={"/accommodations"} exact render={()=> <Accommodations accommodations={this.state.accommodations}/>}/>
                        <Route path={"/categories"} exact render={()=> <Categories categories={this.state.accommodations}/>}/>
                        <Route path={"/hosts"} exact render={()=> <Hosts hosts={this.state.accommodations}/>}/>
                        </Routes>
                    </div>

                </main>
            </Router>

        </div>

    );
  }

  loadAccommodations = () => {
    websiteService.fetchAccommodations()
        .then((data) =>{
          this.setState({
            accommodations:data.data
          })

        })
  }

  componentDidMount() {
    this.loadAccommodations();
  }
}
export default App;
