import React from "react";
import AccommodationTerm from "../AccommodationTerm/accommodationTerm";
import {Link} from "react-router-dom";

class Accommodations extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            page: 0,
            size: 2
        }
    }
    render() {

        const offset = this.state.size * this.state.page;
        const nextPageOffset = offset + this.state.size;
        const pageCount = Math.ceil(this.props.accommodations.length / this.state.size);
        const products = this.getProductsPage(offset, nextPageOffset);
        console.log(products, pageCount)

        return (
            <div className={"container mm-4 mt-5"}>
                <div className={"row"}>
                    <div className={"row"}>
                        <table className={"table table-striped"}>
                            <thead>
                            <tr>
                                <th scope={"col"} style={{maxWidth: '200px'}}>Name</th>
                                <th scope={"col"}>Category</th>
                                <th scope={"col"}>Host</th>
                                <th scope={"col"}>Number of rooms</th>
                                <th scope={"col"}>Rented</th>
                            </tr>
                            </thead>
                            <tbody>
                            {props.accommodations?.map((t) => {
                                return (
                                    <AccommodationTerm term={t} onDelete={props.onDelete} onEdit={props.onEdit}
                                                       onRent={props.onRent}/>
                                );
                            })}
                            </tbody>
                        </table>
                    </div>
                    <div className="col mb-3">
                        <div className="row">
                            <div className="col-sm-12 col-md-12">
                                <Link className={"btn btn-block btn-dark"} to={"/accommodations/add"}>Add new
                                    accommodation</Link>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        );
    }
    handlePageClick = (data) => {
        let selected = data.selected;
        console.log(selected)
        this.setState({
            page: selected
        })
    }

    getAccommodationsPage = (offset, nextPageOffset) => {
        console.log(offset, nextPageOffset)
        return this.props.accommodations.map((term, index) => {
            return (
                <AccommodationTerm term={term} onDelete={this.props.onDelete} onEdit={this.props.onEdit}/>
            );
        }).filter((accommodation, index) => {
            return index >= offset && index < nextPageOffset;
        })
    }

}

export default Accommodations;
